public class Camera {

	// properties
	private float xPos;
	private float yPos;
	
	// constructor
	public Camera() {
		// TODO Auto-generated constructor stub
	}

	public float getxPos() {
		return xPos;
	}

	public void setxPos(float xPos) {
		this.xPos = xPos;
	}

	public float getyPos() {
		return yPos;
	}

	public void setyPos(float yPos) {
		this.yPos = yPos;
	}
}
